import { Awaitable } from 'cosmokit';
import Schema from 'schemastery';
import WebSocket from 'ws';
declare module '@satorijs/core' {
    interface Bot {
        socket?: WebSocket;
    }
    namespace Adapter {
        namespace WsClient {
            interface Config extends Bot.Config {
                retryLazy?: number;
                retryTimes?: number;
                retryInterval?: number;
            }
            const Config: Schema<Config>;
        }
        abstract class WsClient<T extends Bot<Context, WsClient.Config>> extends Adapter.Client<T> {
            protected abstract prepare(bot: T): Awaitable<WebSocket>;
            protected abstract accept(bot: T): void;
        }
    }
}
